// Load bindings binary
var oldDir = process.cwd();
try {
  process.chdir(__dirname+'/lib');
} catch (e) {
  // if no local deps/ dir, assume shared lib linking. keep going
}
var qt = require(__dirname + '/qt.node');
process.chdir(oldDir);

// optional extensions+utilities
require(__dirname + '/qt-constants')(qt);
require(__dirname + '/qt-utils')(qt);

module.exports = qt;